from locust import HttpUser, task, between
import requests


class MyUser(HttpUser):
    wait_time = between(1, 1)

    @task
    def compare_images(self):
        url = "/compare/feature"

        files = {
            "feature_a": ("", ""),
            
            "feature_b": ("", "")
        }

        response = self.client.post(url, files=files)
        assert response.status_code == 200
